pragma solidity ^0.8.10;

import { FixedPoint128 } from '@uniswap/v3-core-0.8-support/contracts/libraries/FixedPoint128.sol';
import { FullMath } from '@uniswap/v3-core-0.8-support/contracts/libraries/FullMath.sol';
import { SafeCast } from '@uniswap/v3-core-0.8-support/contracts/libraries/SafeCast.sol';

import { DepositTokenSet } from './DepositTokenSet.sol';
import { SignedFullMath } from './SignedFullMath.sol';
import { SignedMath } from './SignedMath.sol';
import { LiquidityPositionSet } from './LiquidityPositionSet.sol';
import { LiquidityPosition } from './LiquidityPosition.sol';
import { VTokenLib } from './VTokenLib.sol';
import { RTokenLib } from './RTokenLib.sol';
import { VTokenPosition } from './VTokenPosition.sol';
import { VTokenPositionSet } from './VTokenPositionSet.sol';

import { IClearingHouse } from '../interfaces/IClearingHouse.sol';
import { IVBase } from '../interfaces/IVBase.sol';
import { IVToken } from '../interfaces/IVToken.sol';

import { console } from 'hardhat/console.sol';

library Account {
    using Account for Account.UserInfo;
    using DepositTokenSet for DepositTokenSet.Info;
    using FullMath for uint256;
    using LiquidityPositionSet for LiquidityPositionSet.Info;
    using SafeCast for uint256;
    using SignedFullMath for int256;
    using SignedMath for int256;
    using VTokenLib for IVToken;
    using VTokenPositionSet for VTokenPositionSet.Set;
    using VTokenPosition for VTokenPosition.Position;

    /// @notice account info for user
    /// @param owner specifies the account owner
    /// @param tokenPositions is set of all open token positions
    /// @param tokenDeposits is set of all deposits
    struct UserInfo {
        address owner;
        VTokenPositionSet.Set tokenPositions;
        DepositTokenSet.Info tokenDeposits;
        uint256[100] _emptySlots; // reserved for adding variables when upgrading logic
    }

    struct ProtocolInfo {
        // rage trade pools
        mapping(IVToken => IClearingHouse.RageTradePool) pools;
        // conversion from compressed addressed to full address
        mapping(uint32 => RTokenLib.RToken) rTokens;
        mapping(uint32 => IVToken) vTokens;
        // virtual base
        IVBase vBase;
        // accounting settings
        LiquidationParams liquidationParams;
        uint256 minRequiredMargin;
        uint256 removeLimitOrderFee;
        uint256 minimumOrderNotional;
        // reserved for adding slots in future
        uint256[100] _emptySlots;
    }

    /// @notice parameters to be used for liquidation
    /// @param fixFee specifies the fixFee to be given for successful liquidation
    /// @param minRequiredMargin specifies the minimum required margin threshold
    /// @param liquidationFeeFraction specifies the percentage of notional value liquidated to be charged as liquidation fees
    /// @param tokenLiquidationPriceDeltaBps specifies the price delta from current perp price at which the liquidator should get the position
    /// @param insuranceFundFeeShare specifies the fee share for insurance fund out of the total liquidation fee
    struct LiquidationParams {
        uint16 liquidationFeeFraction;
        uint16 tokenLiquidationPriceDeltaBps;
        uint16 insuranceFundFeeShareBps;
    }

    /// @notice error to denote that there is not enough margin for the transaction to go through
    /// @param accountMarketValue shows the account market value after the transaction is executed
    /// @param totalRequiredMargin shows the total required margin after the transaction is executed
    error InvalidTransactionNotEnoughMargin(int256 accountMarketValue, int256 totalRequiredMargin);

    /// @notice error to denote that there is not enough profit during profit withdrawal
    /// @param totalProfit shows the value of positions at the time of execution after removing amount specified
    error InvalidTransactionNotEnoughProfit(int256 totalProfit);

    /// @notice error to denote that there is enough margin, hence the liquidation is invalid
    /// @param accountMarketValue shows the account market value before liquidation
    /// @param totalRequiredMargin shows the total required margin before liquidation
    error InvalidLiquidationAccountAbovewater(int256 accountMarketValue, int256 totalRequiredMargin);

    /// @notice error to denote that there are active ranges present during token liquidation, hence the liquidation is invalid
    /// @param vToken shows the token address for which range is active
    error InvalidLiquidationActiveRangePresent(IVToken vToken);

    /// @notice denotes new account creation
    /// @param ownerAddress wallet address of account owner
    /// @param accountNo serial number of the account
    event AccountCreated(address indexed ownerAddress, uint256 accountNo);

    /// @notice denotes deposit of margin
    /// @param accountNo serial number of the account
    /// @param rTokenAddress token in which margin is deposited
    /// @param amount amount of tokens deposited
    event DepositMargin(uint256 accountNo, address rTokenAddress, uint256 amount);

    /// @notice denotes withdrawal of margin
    /// @param accountNo serial number of the account
    /// @param rTokenAddress token in which margin is withdrawn
    /// @param amount amount of tokens withdrawn
    event WithdrawMargin(uint256 accountNo, address rTokenAddress, uint256 amount);

    /// @notice denotes withdrawal of profit in base token
    /// @param accountNo serial number of the account
    /// @param amount amount of profit withdrawn
    event WithdrawProfit(uint256 accountNo, uint256 amount);

    /// @notice denotes token position change
    /// @param accountNo serial number of the account
    /// @param vToken address of token whose position was taken
    /// @param tokenAmountOut amount of tokens that account received (positive) or paid (negative)
    /// @param baseAmountOut amount of base tokens that account received (positive) or paid (negative)
    event TokenPositionChange(uint256 accountNo, IVToken vToken, int256 tokenAmountOut, int256 baseAmountOut);

    /// @notice denotes token position change due to liquidity add/remove
    /// @param accountNo serial number of the account
    /// @param vToken address of token whose position was taken
    /// @param tickLower lower tick of the range updated
    /// @param tickUpper upper tick of the range updated
    /// @param tokenAmountOut amount of tokens that account received (positive) or paid (negative)
    event LiquidityTokenPositionChange(
        uint256 accountNo,
        IVToken vToken,
        int24 tickLower,
        int24 tickUpper,
        int256 tokenAmountOut
    );

    /// @notice denotes liquidity add/remove
    /// @param accountNo serial number of the account
    /// @param vToken address of token whose position was taken
    /// @param tickLower lower tick of the range updated
    /// @param tickUpper upper tick of the range updated
    /// @param liquidityDelta change in liquidity value
    /// @param limitOrderType the type of range position
    /// @param tokenAmountOut amount of tokens that account received (positive) or paid (negative)
    /// @param baseAmountOut amount of base tokens that account received (positive) or paid (negative)
    event LiquidityChange(
        uint256 accountNo,
        IVToken vToken,
        int24 tickLower,
        int24 tickUpper,
        int128 liquidityDelta,
        IClearingHouse.LimitOrderType limitOrderType,
        int256 tokenAmountOut,
        int256 baseAmountOut
    );

    /// @notice denotes funding payment for a range / token position
    /// @dev for a token position tickLower = tickUpper = 0
    /// @param accountNo serial number of the account
    /// @param vToken address of token for which funding was paid
    /// @param tickLower lower tick of the range for which funding was paid
    /// @param tickUpper upper tick of the range for which funding was paid
    /// @param amount amount of funding paid (negative) or received (positive)
    event FundingPayment(uint256 accountNo, IVToken vToken, int24 tickLower, int24 tickUpper, int256 amount);

    /// @notice denotes fee payment for a range / token position
    /// @dev for a token position tickLower = tickUpper = 0
    /// @param accountNo serial number of the account
    /// @param vToken address of token for which fee was paid
    /// @param tickLower lower tick of the range for which fee was paid
    /// @param tickUpper upper tick of the range for which fee was paid
    /// @param amount amount of fee paid (negative) or received (positive)
    event LiquidityFee(uint256 accountNo, IVToken vToken, int24 tickLower, int24 tickUpper, int256 amount);

    /// @notice denotes protocol fee withdrawal from a pool wrapper
    /// @param wrapperAddress address of token for which fee was paid
    /// @param feeAmount amount of protocol fee which was withdrawn
    event ProtocolFeeWithdrawm(address wrapperAddress, uint256 feeAmount);

    /// @notice denotes range position liquidation event
    /// @dev all range positions are liquidated and the current tokens inside the range are added in as token positions to the account
    /// @param accountNo serial number of the account
    /// @param keeperAddress address of keeper who performed the liquidation
    /// @param liquidationFee total liquidation fee charged to the account
    /// @param keeperFee total liquidaiton fee paid to the keeper (positive only)
    /// @param insuranceFundFee total liquidaiton fee paid to the insurance fund (can be negative in case the account is not enought to cover the fee)
    event LiquidateRanges(
        uint256 accountNo,
        address keeperAddress,
        int256 liquidationFee,
        int256 keeperFee,
        int256 insuranceFundFee
    );

    /// @notice denotes token position liquidation event
    /// @dev the selected token position is take from the current account and moved to liquidatorAccount at a discounted prive to current pool price
    /// @param accountNo serial number of the account
    /// @param liquidatorAccountNo  account which performed the liquidation
    /// @param vToken address of token for whose position was liquidated
    /// @param liquidationBps the fraction of current position which was liquidated in bps
    /// @param liquidationPriceX128 price at which liquidation was performed
    /// @param liquidatorPriceX128 discounted price at which tokens were transferred to the liquidator account
    /// @param insuranceFundFee total liquidaiton fee paid to the insurance fund (can be negative in case the account is not enough to cover the fee)
    event LiquidateTokenPosition(
        uint256 accountNo,
        uint256 liquidatorAccountNo,
        IVToken vToken,
        uint16 liquidationBps,
        uint256 liquidationPriceX128,
        uint256 liquidatorPriceX128,
        int256 insuranceFundFee
    );

    /// @notice checks if 'account' is initialized
    /// @param account pointer to 'account' struct
    function isInitialized(UserInfo storage account) internal view returns (bool) {
        return account.owner != address(0);
    }

    /// @notice updates the base balance for 'account' by 'amount'
    /// @param account pointer to 'account' struct
    /// @param amount amount of balance to update
    /// @param protocol platform constants
    function updateBaseBalance(
        UserInfo storage account,
        int256 amount,
        Account.ProtocolInfo storage protocol
    ) internal returns (IClearingHouse.BalanceAdjustments memory balanceAdjustments) {
        balanceAdjustments = IClearingHouse.BalanceAdjustments(amount, 0, 0);
        account.tokenPositions.update(balanceAdjustments, IVToken(address(protocol.vBase)), protocol);
    }

    /// @notice increases deposit balance of 'vToken' by 'amount'
    /// @param account account to deposit balance into
    /// @param realTokenAddress address of token to deposit
    /// @param amount amount of token to deposit
    function addMargin(
        UserInfo storage account,
        address realTokenAddress,
        uint256 amount
    ) external {
        // vBASE should be an immutable constant
        account.tokenDeposits.increaseBalance(realTokenAddress, amount);
    }

    /// @notice reduces deposit balance of 'vToken' by 'amount'
    /// @param account account to deposit balance into
    /// @param realTokenAddress address of token to remove
    /// @param amount amount of token to remove
    /// @param protocol set of all constants and token addresses
    function removeMargin(
        UserInfo storage account,
        address realTokenAddress,
        uint256 amount,
        Account.ProtocolInfo storage protocol
    ) external {
        account.tokenDeposits.decreaseBalance(realTokenAddress, amount);

        account.checkIfMarginAvailable(true, protocol);
    }

    /// @notice removes 'amount' of profit generated in base token
    /// @param account account to remove profit from
    /// @param amount amount of profit(base token) to remove
    /// @param protocol set of all constants and token addresses
    function removeProfit(
        UserInfo storage account,
        uint256 amount,
        Account.ProtocolInfo storage protocol
    ) external {
        account.updateBaseBalance(-int256(amount), protocol);

        account.checkIfProfitAvailable(protocol);
        account.checkIfMarginAvailable(true, protocol);
    }

    /// @notice returns market value and required margin for the account based on current market conditions
    /// @dev (In case requiredMargin < minRequiredMargin then requiredMargin = minRequiredMargin)
    /// @param account account to check
    /// @param isInitialMargin true to use initial margin factor and false to use maintainance margin factor for calcualtion of required margin
    /// @param protocol set of all constants and token addresses
    /// @return accountMarketValue total market value of all the positions (token ) and deposits
    /// @return totalRequiredMargin total margin required to keep the account above selected margin requirement (intial/maintainance)
    function getAccountValueAndRequiredMargin(
        UserInfo storage account,
        bool isInitialMargin,
        Account.ProtocolInfo storage protocol
    ) internal view returns (int256 accountMarketValue, int256 totalRequiredMargin) {
        accountMarketValue = account.getAccountValue(protocol);

        totalRequiredMargin = account.tokenPositions.getRequiredMargin(isInitialMargin, protocol.vTokens, protocol);
        if (!account.tokenPositions.isEmpty()) {
            totalRequiredMargin = totalRequiredMargin < int256(protocol.minRequiredMargin)
                ? int256(protocol.minRequiredMargin)
                : totalRequiredMargin;
        }
        return (accountMarketValue, totalRequiredMargin);
    }

    /// @notice returns market value for the account based on current market conditions
    /// @param account account to check
    /// @param protocol set of all constants and token addresses
    /// @return accountMarketValue total market value of all the positions (token ) and deposits
    function getAccountValue(UserInfo storage account, Account.ProtocolInfo storage protocol)
        internal
        view
        returns (int256 accountMarketValue)
    {
        accountMarketValue = account.tokenPositions.getAccountMarketValue(protocol.vTokens, protocol);
        accountMarketValue += account.tokenDeposits.getAllDepositAccountMarketValue(protocol);
        return (accountMarketValue);
    }

    /// @notice checks if market value > required margin else revert with InvalidTransactionNotEnoughMargin
    /// @param account account to check
    /// @param isInitialMargin true to use initialMarginFactor and false to use maintainance margin factor for calcualtion of required margin
    /// @param protocol set of all constants and token addresses
    function checkIfMarginAvailable(
        UserInfo storage account,
        bool isInitialMargin,
        Account.ProtocolInfo storage protocol
    ) internal view {
        (int256 accountMarketValue, int256 totalRequiredMargin) = account.getAccountValueAndRequiredMargin(
            isInitialMargin,
            protocol
        );
        if (accountMarketValue < totalRequiredMargin)
            revert InvalidTransactionNotEnoughMargin(accountMarketValue, totalRequiredMargin);
    }

    /// @notice checks if profit is available to withdraw base token (token value of all positions > 0) else revert with InvalidTransactionNotEnoughProfit
    /// @param account account to check
    /// @param protocol set of all constants and token addresses
    function checkIfProfitAvailable(UserInfo storage account, Account.ProtocolInfo storage protocol) internal view {
        int256 totalPositionValue = account.tokenPositions.getAccountMarketValue(protocol.vTokens, protocol);
        if (totalPositionValue < 0) revert InvalidTransactionNotEnoughProfit(totalPositionValue);
    }

    /// @notice swaps 'vToken' of token amount equal to 'swapParams.amount'
    /// @notice if vTokenAmount>0 then the swap is a long or close short and if vTokenAmount<0 then swap is a short or close long
    /// @notice isNotional specifies whether the amount represents token amount (false) or base amount(true)
    /// @notice isPartialAllowed specifies whether to revert (false) or to execute a partial swap (true)
    /// @notice sqrtPriceLimit threshold sqrt price which if crossed then revert or execute partial swap
    /// @param account account to swap tokens for
    /// @param vToken address of the token to swap
    /// @param swapParams parameters for the swap (Includes - amount, sqrtPriceLimit, isNotional, isPartialAllowed)
    /// @param protocol set of all constants and token addresses
    function swapToken(
        UserInfo storage account,
        IVToken vToken,
        IClearingHouse.SwapParams memory swapParams,
        Account.ProtocolInfo storage protocol
    ) external returns (int256 vTokenAmountOut, int256 vBaseAmountOut) {
        // make a swap. vBaseIn and vTokenAmountOut (in and out wrt uniswap).
        // mints erc20 tokens in callback and send to the pool
        (vTokenAmountOut, vBaseAmountOut) = account.tokenPositions.swapToken(vToken, swapParams, protocol);

        // after all the stuff, account should be above water
        account.checkIfMarginAvailable(true, protocol);
    }

    /// @notice changes range liquidity 'vToken' of market value equal to 'vTokenNotional'
    /// @notice if 'liquidityDelta'>0 then liquidity is added and if 'liquidityChange'<0 then liquidity is removed
    /// @notice the liquidity change is reverted if the sqrt price at the time of execution is beyond 'slippageToleranceBps' of 'sqrtPriceCurrent' supplied
    /// @notice whenever liquidity change is done the internal token position is taken out. If 'closeTokenPosition' is true this is swapped out else it is added to the current token position
    /// @param account account to change liquidity
    /// @param vToken address of token to swap
    /// @param liquidityChangeParams parameters including lower tick, upper tick, liquidity delta, sqrtPriceCurrent, slippageToleranceBps, closeTokenPosition, limit order type
    /// @param protocol set of all constants and token addresses
    function liquidityChange(
        UserInfo storage account,
        IVToken vToken,
        IClearingHouse.LiquidityChangeParams memory liquidityChangeParams,
        Account.ProtocolInfo storage protocol
    ) external returns (int256 vTokenAmountOut, int256 vBaseAmountOut) {
        // mint/burn tokens + fee + funding payment
        (vTokenAmountOut, vBaseAmountOut) = account.tokenPositions.liquidityChange(
            vToken,
            liquidityChangeParams,
            protocol
        );

        // after all the stuff, account should be above water
        account.checkIfMarginAvailable(true, protocol);
    }

    /// @notice computes keeper fee and insurance fund fee in case of liquidity position liquidation
    /// @dev keeperFee = liquidationFee*(1-insuranceFundFeeShare)+fixFee
    /// @dev insuranceFundFee = accountMarketValue - keeperFee (if accountMarketValue is not enough to cover the fees) else insurancFundFee = liquidationFee - keeperFee + fixFee
    /// @param accountMarketValue market value of account
    /// @param liquidationFee total liquidation fee to be charged to the account in case of an on time liquidation
    /// @param liquidationParams parameters including fixFee, insuranceFundFeeShareBps
    /// @return keeperFee map of vTokens allowed on the platform
    /// @return insuranceFundFee poolwrapper for token
    function computeLiquidationFees(
        int256 accountMarketValue,
        int256 liquidationFee,
        uint256 fixFee,
        LiquidationParams memory liquidationParams
    ) internal pure returns (int256 keeperFee, int256 insuranceFundFee) {
        int256 fixFeeInt = int256(fixFee);
        keeperFee = liquidationFee.mulDiv(1e4 - liquidationParams.insuranceFundFeeShareBps, 1e4) + fixFeeInt;
        if (accountMarketValue - fixFeeInt - liquidationFee < 0) {
            insuranceFundFee = accountMarketValue - keeperFee;
        } else {
            insuranceFundFee = liquidationFee - keeperFee + fixFeeInt;
        }
    }

    /// @notice liquidates all range positions in case the account is under water
    /// @notice charges a liquidation fee to the account and pays partially to the insurance fund and rest to the keeper.
    /// @dev insurance fund covers the remaining fee if the account market value is not enough
    /// @param account account to liquidate
    /// @param protocol set of all constants and token addresses
    function liquidateLiquidityPositions(
        UserInfo storage account,
        uint256 fixFee,
        Account.ProtocolInfo storage protocol
    ) external returns (int256 keeperFee, int256 insuranceFundFee) {
        // check basis maintanace margin
        int256 accountMarketValue;
        int256 totalRequiredMargin;
        int256 notionalAmountClosed;

        (accountMarketValue, totalRequiredMargin) = account.getAccountValueAndRequiredMargin(false, protocol);
        if (accountMarketValue > totalRequiredMargin) {
            revert InvalidLiquidationAccountAbovewater(accountMarketValue, totalRequiredMargin);
        }
        notionalAmountClosed = account.tokenPositions.liquidateLiquidityPositions(protocol.vTokens, protocol);

        int256 liquidationFee = notionalAmountClosed.mulDiv(protocol.liquidationParams.liquidationFeeFraction, 1e5);
        (keeperFee, insuranceFundFee) = computeLiquidationFees(
            accountMarketValue,
            liquidationFee,
            fixFee,
            protocol.liquidationParams
        );

        account.updateBaseBalance(-(keeperFee + insuranceFundFee), protocol);
    }

    /// @notice computes the liquidation & liquidator price and insurance fund fee for token liquidation
    /// @param tokensToTrade account to liquidate
    /// @param vToken map of vTokens allowed on the platform
    /// @param protocol set of all constants and token addresses
    function getLiquidationPriceX128AndFee(
        int256 tokensToTrade,
        IVToken vToken,
        Account.ProtocolInfo storage protocol
    )
        internal
        view
        returns (
            uint256 liquidationPriceX128,
            uint256 liquidatorPriceX128,
            int256 insuranceFundFee
        )
    {
        uint16 maintainanceMarginFactor = vToken.getMarginRatio(false, protocol);
        uint256 priceX128 = vToken.getVirtualCurrentPriceX128(protocol);
        uint256 priceDeltaX128 = priceX128.mulDiv(protocol.liquidationParams.tokenLiquidationPriceDeltaBps, 1e4).mulDiv(
            maintainanceMarginFactor,
            1e5
        );
        if (tokensToTrade < 0) {
            liquidationPriceX128 = priceX128 - priceDeltaX128;
            liquidatorPriceX128 =
                priceX128 -
                priceDeltaX128.mulDiv(1e4 - protocol.liquidationParams.insuranceFundFeeShareBps, 1e4);
            insuranceFundFee = -tokensToTrade.mulDiv(liquidatorPriceX128 - liquidationPriceX128, FixedPoint128.Q128);
        } else {
            liquidationPriceX128 = priceX128 + priceDeltaX128;
            liquidatorPriceX128 =
                priceX128 +
                priceDeltaX128.mulDiv(1e4 - protocol.liquidationParams.insuranceFundFeeShareBps, 1e4);
            insuranceFundFee = tokensToTrade.mulDiv(liquidationPriceX128 - liquidatorPriceX128, FixedPoint128.Q128);
        }
    }

    /// @notice exchanges token position between account (at liquidationPrice) and liquidator account (at liquidator price)
    /// @notice also charges fixFee from the account and pays to liquidator
    /// @param account is account being liquidated
    /// @param liquidatorAccount is account of liquidator
    /// @param vToken map of vTokens allowed on the platform
    /// @param tokensToTrade number of tokens to trade
    /// @param liquidationPriceX128 price at which tokens should be traded out
    /// @param liquidatorPriceX128 discounted price at which tokens should be given to liquidator
    /// @param fixFee is the fee to be given to liquidator to compensate for gas price
    /// @param protocol platform constants
    function updateLiquidationAccounts(
        UserInfo storage account,
        UserInfo storage liquidatorAccount,
        IVToken vToken,
        int256 tokensToTrade,
        uint256 liquidationPriceX128,
        uint256 liquidatorPriceX128,
        int256 fixFee,
        Account.ProtocolInfo storage protocol
    ) internal returns (IClearingHouse.BalanceAdjustments memory liquidatorBalanceAdjustments) {
        vToken.vPoolWrapper(protocol).updateGlobalFundingState();

        IClearingHouse.BalanceAdjustments memory balanceAdjustments = IClearingHouse.BalanceAdjustments({
            vBaseIncrease: -tokensToTrade.mulDiv(liquidationPriceX128, FixedPoint128.Q128) - fixFee,
            vTokenIncrease: tokensToTrade,
            traderPositionIncrease: tokensToTrade
        });

        account.tokenPositions.update(balanceAdjustments, vToken, protocol);
        emit Account.TokenPositionChange(
            account.tokenPositions.accountNo,
            vToken,
            balanceAdjustments.vTokenIncrease,
            balanceAdjustments.vBaseIncrease
        );

        balanceAdjustments = IClearingHouse.BalanceAdjustments({
            vBaseIncrease: tokensToTrade.mulDiv(liquidatorPriceX128, FixedPoint128.Q128) + fixFee,
            vTokenIncrease: -tokensToTrade,
            traderPositionIncrease: -tokensToTrade
        });

        liquidatorAccount.tokenPositions.update(balanceAdjustments, vToken, protocol);
        emit Account.TokenPositionChange(
            liquidatorAccount.tokenPositions.accountNo,
            vToken,
            balanceAdjustments.vTokenIncrease,
            balanceAdjustments.vBaseIncrease
        );

        return balanceAdjustments;
    }

    /// @notice liquidates all range positions in case the account is under water
    /// @param account account to liquidate
    /// @param vToken address of token to swap
    /// @param protocol set of all constants and token addresses
    function liquidateTokenPosition(
        UserInfo storage account,
        UserInfo storage liquidatorAccount,
        uint16 liquidationBps,
        IVToken vToken,
        uint256 fixFee,
        Account.ProtocolInfo storage protocol
    )
        external
        returns (int256 insuranceFundFee, IClearingHouse.BalanceAdjustments memory liquidatorBalanceAdjustments)
    {
        if (account.tokenPositions.getIsTokenRangeActive(vToken, protocol))
            revert InvalidLiquidationActiveRangePresent(vToken);

        {
            (int256 accountMarketValue, int256 totalRequiredMargin) = account.getAccountValueAndRequiredMargin(
                false,
                protocol
            );

            if (accountMarketValue > totalRequiredMargin) {
                revert InvalidLiquidationAccountAbovewater(accountMarketValue, totalRequiredMargin);
            }
        }

        int256 tokensToTrade;
        {
            VTokenPosition.Position storage vTokenPosition = account.tokenPositions.getTokenPosition(
                vToken,
                false,
                protocol
            );
            tokensToTrade = -vTokenPosition.balance.mulDiv(liquidationBps, 1e4);
        }

        uint256 liquidationPriceX128;
        uint256 liquidatorPriceX128;
        {
            (liquidationPriceX128, liquidatorPriceX128, insuranceFundFee) = getLiquidationPriceX128AndFee(
                tokensToTrade,
                vToken,
                protocol
            );

            liquidatorBalanceAdjustments = updateLiquidationAccounts(
                account,
                liquidatorAccount,
                vToken,
                tokensToTrade,
                liquidationPriceX128,
                liquidatorPriceX128,
                int256(fixFee),
                protocol
            );
        }
        {
            int256 accountMarketValueFinal = account.getAccountValue(protocol);

            if (accountMarketValueFinal < 0) {
                insuranceFundFee = accountMarketValueFinal;
                account.updateBaseBalance(-accountMarketValueFinal, protocol);
            }
        }

        liquidatorAccount.checkIfMarginAvailable(false, protocol);
        emit Account.LiquidateTokenPosition(
            account.tokenPositions.accountNo,
            liquidatorAccount.tokenPositions.accountNo,
            vToken,
            liquidationBps,
            liquidationPriceX128,
            liquidatorPriceX128,
            insuranceFundFee
        );
    }

    /// @notice removes limit order based on the current price position (keeper call)
    /// @param account account to liquidate
    /// @param vToken address of token for the range
    /// @param tickLower lower tick index for the range
    /// @param tickUpper upper tick index for the range
    /// @param protocol platform constants
    function removeLimitOrder(
        UserInfo storage account,
        IVToken vToken,
        int24 tickLower,
        int24 tickUpper,
        uint256 limitOrderFeeAndFixFee,
        Account.ProtocolInfo storage protocol
    ) external {
        account.tokenPositions.removeLimitOrder(vToken, tickLower, tickUpper, protocol);

        account.updateBaseBalance(-int256(limitOrderFeeAndFixFee), protocol);
    }
}
","keccak256":"0x2273227533e5fdb58c7c54319b10185b4fcdbda35409e68c1efbe10c0250d4ed","license":"UNLICENSED"},"contracts/libraries/DepositTokenSet.sol":{"content":"//SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.9;

import { FixedPoint128 } from '@uniswap/v3-core-0.8-support/contracts/libraries/FixedPoint128.sol';

import { Account } from './Account.sol';
import { RTokenLib } from './RTokenLib.sol';
import { SignedFullMath } from './SignedFullMath.sol';
import { Uint32L8ArrayLib } from './Uint32L8Array.sol';

import { IClearingHouse } from '../interfaces/IClearingHouse.sol';

import { console } from 'hardhat/console.sol';

library DepositTokenSet {
    using RTokenLib for RTokenLib.RToken;
    using RTokenLib for address;
    using Uint32L8ArrayLib for uint32[8];
    using SignedFullMath for int256;

    struct Info {
        // fixed length array of truncate(tokenAddress)
        // open positions in 8 different pairs at same time.
        // single per pool because it's fungible, allows for having
        uint32[8] active;
        mapping(uint32 => uint256) deposits;
        uint256[100] _emptySlots; // reserved for adding variables when upgrading logic
    }

    // add overrides that accept vToken or truncated
    function increaseBalance(
        Info storage info,
        address realTokenAddress,
        uint256 amount
    ) internal {
        uint32 truncated = realTokenAddress.truncate();

        // consider vbase as always active because it is base (actives are needed for margin check)
        info.active.include(truncated);

        info.deposits[realTokenAddress.truncate()] += amount;
    }

    function decreaseBalance(
        Info storage info,
        address realTokenAddress,
        uint256 amount
    ) internal {
        uint32 truncated = realTokenAddress.truncate();

        require(info.deposits[truncated] >= amount);
        info.deposits[truncated] -= amount;

        if (info.deposits[truncated] == 0) {
            info.active.exclude(truncated);
        }
    }

    function getAllDepositAccountMarketValue(Info storage set, Account.ProtocolInfo storage protocol)
        internal
        view
        returns (int256)
    {
        int256 accountMarketValue;
        for (uint8 i = 0; i < set.active.length; i++) {
            uint32 truncated = set.active[i];

            if (truncated == 0) break;
            RTokenLib.RToken storage token = protocol.rTokens[truncated];

            accountMarketValue += int256(set.deposits[truncated]).mulDiv(
                token.getRealTwapPriceX128(),
                FixedPoint128.Q128
            );
        }
        return accountMarketValue;
    }

    function getView(Info storage set, Account.ProtocolInfo storage protocol)
        internal
        view
        returns (IClearingHouse.DepositTokenView[] memory depositTokens)
    {
        uint256 numberOfTokenPositions = set.active.numberOfNonZeroElements();
        depositTokens = new IClearingHouse.DepositTokenView[](numberOfTokenPositions);

        for (uint256 i = 0; i < numberOfTokenPositions; i++) {
            depositTokens[i].rTokenAddress = address(protocol.rTokens[set.active[i]].tokenAddress);
            depositTokens[i].balance = set.deposits[set.active[i]];
        }
    }
}
","keccak256":"0xe3540ad7824b5843882838ea70648ae0b835532047a3e6dba341223aafd25a93","license":"UNLICENSED"},"contracts/libraries/FundingPayment.sol":{"content":"//SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.9;

import { FixedPoint128 } from '@uniswap/v3-core-0.8-support/contracts/libraries/FixedPoint128.sol';
import { FullMath } from '@uniswap/v3-core-0.8-support/contracts/libraries/FullMath.sol';
import { SignedFullMath } from './SignedFullMath.sol';

import { console } from 'hardhat/console.sol';

/// @notice Funding Payment Logic used to distribute the FP bill paid by traders among the LPs in the liquidity range
library FundingPayment {
    using FullMath for uint256;
    using SignedFullMath for int256;

    struct Info {
        // FR * P * dt
        int256 sumAX128;
        // trade token amount / liqidity
        int256 sumBX128;
        // sum(a * sumB)
        int256 sumFpX128;
        // time when state was last updated
        uint48 timestampLast;
    }

    function update(
        Info storage info,
        int256 tokenAmount,
        uint256 liquidity,
        uint48 blockTimestamp,
        uint256 realPriceX128,
        uint256 virtualPriceX128
    ) internal {
        int256 a = nextAX128(info.timestampLast, blockTimestamp, realPriceX128, virtualPriceX128);
        info.sumFpX128 += a.mulDivRoundingDown(info.sumBX128, int256(FixedPoint128.Q128));
        info.sumAX128 += a;
        info.sumBX128 += tokenAmount.mulDiv(int256(FixedPoint128.Q128), int256(liquidity));
        info.timestampLast = blockTimestamp;
    }

    /// @notice Positive A value means at this duration, longs pay shorts. Negative means shorts pay longs.
    /// @param timestampLast start timestamp of duration
    /// @param blockTimestamp end timestamp of duration
    /// @param realPriceX128 spot price of token, used to calculate funding rate
    /// @param virtualPriceX128 futures price of token, used to calculate funding rate
    /// @return aX128 value called "a" (see funding payment math documentation)
    function nextAX128(
        uint48 timestampLast,
        uint48 blockTimestamp,
        uint256 realPriceX128,
        uint256 virtualPriceX128
    ) internal pure returns (int256 aX128) {
        return
            (int256(realPriceX128) - int256(virtualPriceX128)).mulDiv(virtualPriceX128, realPriceX128).mulDiv(
                blockTimestamp - timestampLast,
                1 days
            );
    }

    function extrapolatedSumAX128(
        int256 sumAX128,
        uint48 timestampLast,
        uint48 blockTimestamp,
        uint256 realPriceX128,
        uint256 virtualPriceX128
    ) internal pure returns (int256) {
        return sumAX128 + nextAX128(timestampLast, blockTimestamp, realPriceX128, virtualPriceX128);
    }

    /// @notice Extrapolates (updates) the value of sumFp by adding the missing component to it using sumAGlobalX128
    /// @param sumAX128 sumA value that is recorded from global at some point in time
    /// @param sumBX128 sumB value that is recorded from global at same point in time as sumA
    /// @param sumFpX128 sumFp value that is recorded from global at same point in time as sumA and sumB
    /// @param sumAGlobalX128 latest sumA value (taken from global), used to extrapolate the sumFp
    function extrapolatedSumFpX128(
        int256 sumAX128,
        int256 sumBX128,
        int256 sumFpX128,
        int256 sumAGlobalX128
    ) internal pure returns (int256) {
        return sumFpX128 + sumBX128.mulDiv(sumAGlobalX128 - sumAX128, int256(FixedPoint128.Q128));
    }

    /// @notice Positive bill is rewarded to LPs, Negative bill is charged from LPs
    /// @param sumAX128 latest value of sumA (to be taken from global state)
    /// @param sumFpInsideX128 latest value of sumFp inside range (to be computed using global state + tick state)
    /// @param sumAChkptX128 value of sumA when LP updated their liquidity last time
    /// @param sumBInsideChkptX128 value of sumB inside range when LP updated their liquidity last time
    /// @param sumFpInsideChkptX128 value of sumFp inside range when LP updated their liquidity last time
    /// @param liquidity amount of liquidity which was constant for LP in the time duration
    function bill(
        int256 sumAX128,
        int256 sumFpInsideX128,
        int256 sumAChkptX128,
        int256 sumBInsideChkptX128,
        int256 sumFpInsideChkptX128,
        uint256 liquidity
    ) internal pure returns (int256) {
        return
            (sumFpInsideX128 -
                extrapolatedSumFpX128(sumAChkptX128, sumBInsideChkptX128, sumFpInsideChkptX128, sumAX128))
                .mulDivRoundingDown(liquidity, FixedPoint128.Q128);
    }

    /// @notice Positive bill is rewarded to Traders, Negative bill is charged from Traders
    /// @param sumAX128 latest value of sumA (to be taken from global state)
    /// @param sumAChkptX128 value of sumA when trader updated their netTraderPosition
    /// @param netTraderPosition oken amount which should be constant for time duration since sumAChkptX128 was recorded
    function bill(
        int256 sumAX128,
        int256 sumAChkptX128,
        int256 netTraderPosition
    ) internal pure returns (int256) {
        return netTraderPosition.mulDiv((sumAX128 - sumAChkptX128), int256(FixedPoint128.Q128));
    }
}
","keccak256":"0x8193b19a15a29321a828b7a1d1812f9b9480feb83fa880ed774c32255bb8b8ba","license":"UNLICENSED"},"contracts/libraries/LiquidityPosition.sol":{"content":"//SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.9;

import { SqrtPriceMath } from '@uniswap/v3-core-0.8-support/contracts/libraries/SqrtPriceMath.sol';
import { TickMath } from '@uniswap/v3-core-0.8-support/contracts/libraries/TickMath.sol';
import { SafeCast } from '@uniswap/v3-core-0.8-support/contracts/libraries/SafeCast.sol';
import { FixedPoint128 } from '@uniswap/v3-core-0.8-support/contracts/libraries/FixedPoint128.sol';
import { FullMath } from '@uniswap/v3-core-0.8-support/contracts/libraries/FullMath.sol';
import { IUniswapV3Pool } from '@uniswap/v3-core-0.8-support/contracts/interfaces/IUniswapV3Pool.sol';

import { Account } from './Account.sol';
import { PriceMath } from './PriceMath.sol';
import { SignedFullMath } from './SignedFullMath.sol';
import { VTokenLib } from './VTokenLib.sol';
import { UniswapV3PoolHelper } from './UniswapV3PoolHelper.sol';
import { FundingPayment } from './FundingPayment.sol';

import { IClearingHouse } from '../interfaces/IClearingHouse.sol';
import { IVPoolWrapper } from '../interfaces/IVPoolWrapper.sol';
import { IVToken } from '../interfaces/IVToken.sol';

import { console } from 'hardhat/console.sol';

library LiquidityPosition {
    using PriceMath for uint160;
    using SignedFullMath for int256;
    using FullMath for uint256;
    using SafeCast for uint256;
    using LiquidityPosition for Info;
    using VTokenLib for IVToken;
    using SignedFullMath for int256;
    using UniswapV3PoolHelper for IUniswapV3Pool;

    struct Info {
        //Extra boolean to check if it is limit order and uint to track limit price.
        IClearingHouse.LimitOrderType limitOrderType;
        // the tick range of the position;
        int24 tickLower;
        int24 tickUpper;
        // the liquidity of the position
        uint128 liquidity;
        int256 vTokenAmountIn;
        // funding payment checkpoints
        int256 sumALastX128;
        int256 sumBInsideLastX128;
        int256 sumFpInsideLastX128;
        // fee growth inside
        uint256 sumFeeInsideLastX128;
        uint256[100] _emptySlots; // reserved for adding variables when upgrading logic
    }

    error AlreadyInitialized();
    error IneligibleLimitOrderRemoval();

    function isInitialized(Info storage info) internal view returns (bool) {
        return info.tickLower != 0 || info.tickUpper != 0;
    }

    function checkValidLimitOrderRemoval(Info storage info, int24 currentTick) internal view {
        if (
            !((currentTick >= info.tickUpper && info.limitOrderType == IClearingHouse.LimitOrderType.UPPER_LIMIT) ||
                (currentTick <= info.tickLower && info.limitOrderType == IClearingHouse.LimitOrderType.LOWER_LIMIT))
        ) {
            revert IneligibleLimitOrderRemoval();
        }
    }

    function initialize(
        Info storage position,
        int24 tickLower,
        int24 tickUpper
    ) internal {
        if (position.isInitialized()) {
            revert AlreadyInitialized();
        }

        position.tickLower = tickLower;
        position.tickUpper = tickUpper;
    }

    function liquidityChange(
        Info storage position,
        uint256 accountNo,
        IVToken vToken,
        int128 liquidity,
        IVPoolWrapper wrapper,
        IClearingHouse.BalanceAdjustments memory balanceAdjustments
    ) internal {
        (
            int256 basePrincipal,
            int256 vTokenPrincipal,
            IVPoolWrapper.WrapperValuesInside memory wrapperValuesInside
        ) = wrapper.liquidityChange(position.tickLower, position.tickUpper, liquidity);

        position.update(accountNo, vToken, wrapperValuesInside, balanceAdjustments);

        balanceAdjustments.vBaseIncrease -= basePrincipal;
        balanceAdjustments.vTokenIncrease -= vTokenPrincipal;

        emit Account.LiquidityChange(
            accountNo,
            vToken,
            position.tickLower,
            position.tickUpper,
            liquidity,
            position.limitOrderType,
            -vTokenPrincipal,
            -basePrincipal
        );

        uint160 sqrtPriceCurrent = wrapper.vPool().sqrtPriceCurrent();
        {
            (int256 tokenAmountCurrent, ) = position.tokenAmountsInRange(sqrtPriceCurrent);

            balanceAdjustments.traderPositionIncrease += tokenAmountCurrent - position.vTokenAmountIn;
        }

        if (liquidity > 0) {
            position.liquidity += uint128(liquidity);
            position.vTokenAmountIn = vTokenPrincipal;
        } else if (liquidity < 0) {
            position.liquidity -= uint128(liquidity * -1);
            position.vTokenAmountIn = 0;
        }
    }

    function update(
        Info storage position,
        uint256 accountNo,
        IVToken vToken,
        IVPoolWrapper.WrapperValuesInside memory wrapperValuesInside,
        IClearingHouse.BalanceAdjustments memory balanceAdjustments
    ) internal {
        int256 fundingPayment = position.unrealizedFundingPayment(
            wrapperValuesInside.sumAX128,
            wrapperValuesInside.sumFpInsideX128
        );
        balanceAdjustments.vBaseIncrease += fundingPayment;

        int256 unrealizedLiquidityFee = position.unrealizedFees(wrapperValuesInside.sumFeeInsideX128).toInt256();
        balanceAdjustments.vBaseIncrease += unrealizedLiquidityFee;

        emit Account.FundingPayment(accountNo, vToken, position.tickLower, position.tickUpper, fundingPayment);
        emit Account.LiquidityFee(accountNo, vToken, position.tickLower, position.tickUpper, unrealizedLiquidityFee);
        // updating checkpoints
        position.sumALastX128 = wrapperValuesInside.sumAX128;
        position.sumBInsideLastX128 = wrapperValuesInside.sumBInsideX128;
        position.sumFpInsideLastX128 = wrapperValuesInside.sumFpInsideX128;
        position.sumFeeInsideLastX128 = wrapperValuesInside.sumFeeInsideX128;
    }

    function netPosition(Info storage position, IVPoolWrapper wrapper) internal view returns (int256) {
        IVPoolWrapper.WrapperValuesInside memory wrapperValuesInside = wrapper.getValuesInside(
            position.tickLower,
            position.tickUpper
        );
        return position.netPosition(wrapperValuesInside.sumBInsideX128);
    }

    function netPosition(Info storage position, int256 sumBInsideX128) internal view returns (int256) {
        return (sumBInsideX128 - position.sumBInsideLastX128).mulDiv(position.liquidity, FixedPoint128.Q128);
    }

    // use funding payment lib
    function unrealizedFundingPayment(
        Info storage position,
        int256 sumAX128,
        int256 sumFpInsideX128
    ) internal view returns (int256 vBaseIncrease) {
        vBaseIncrease = -FundingPayment.bill(
            sumAX128,
            sumFpInsideX128,
            position.sumALastX128,
            position.sumBInsideLastX128,
            position.sumFpInsideLastX128,
            position.liquidity
        );
    }

    function unrealizedFees(Info storage position, uint256 sumFeeInsideX128)
        internal
        view
        returns (uint256 vBaseIncrease)
    {
        vBaseIncrease = (sumFeeInsideX128 - position.sumFeeInsideLastX128).mulDiv(
            position.liquidity,
            FixedPoint128.Q128
        );
    }

    function maxNetPosition(Info storage position) internal view returns (uint256) {
        uint160 sqrtPriceLowerX96 = TickMath.getSqrtRatioAtTick(position.tickLower);
        uint160 sqrtPriceUpperX96 = TickMath.getSqrtRatioAtTick(position.tickUpper);

        return SqrtPriceMath.getAmount0Delta(sqrtPriceLowerX96, sqrtPriceUpperX96, position.liquidity, true);
    }

    function baseValue(
        Info storage position,
        uint160 sqrtPriceCurrent,
        IVToken vToken,
        Account.ProtocolInfo storage protocol
    ) internal view returns (int256 baseValue_) {
        return position.baseValue(sqrtPriceCurrent, vToken.vPoolWrapper(protocol));
    }

    function tokenAmountsInRange(Info storage position, uint160 sqrtPriceCurrent)
        internal
        view
        returns (int256 vTokenAmount, int256 vBaseAmount)
    {
        uint160 sqrtPriceLowerX96 = TickMath.getSqrtRatioAtTick(position.tickLower);
        uint160 sqrtPriceUpperX96 = TickMath.getSqrtRatioAtTick(position.tickUpper);

        // If price is outside the range, then consider it at the ends
        // for calculation of amounts
        uint160 sqrtPriceMiddleX96 = sqrtPriceCurrent;
        if (sqrtPriceCurrent < sqrtPriceLowerX96) {
            sqrtPriceMiddleX96 = sqrtPriceLowerX96;
        } else if (sqrtPriceCurrent > sqrtPriceUpperX96) {
            sqrtPriceMiddleX96 = sqrtPriceUpperX96;
        }

        vTokenAmount = SqrtPriceMath
            .getAmount0Delta(sqrtPriceMiddleX96, sqrtPriceUpperX96, position.liquidity, false)
            .toInt256();
        vBaseAmount = SqrtPriceMath
            .getAmount1Delta(sqrtPriceLowerX96, sqrtPriceMiddleX96, position.liquidity, false)
            .toInt256();
    }

    function baseValue(
        Info storage position,
        uint160 sqrtPriceCurrent,
        IVPoolWrapper wrapper
    ) internal view returns (int256 baseValue_) {
        {
            (int256 vTokenAmount, int256 vBaseAmount) = position.tokenAmountsInRange(sqrtPriceCurrent);
            uint256 priceX128 = sqrtPriceCurrent.toPriceX128();
            baseValue_ = vTokenAmount.mulDiv(priceX128, FixedPoint128.Q128) + vBaseAmount;
        }
        // adding fees
        IVPoolWrapper.WrapperValuesInside memory wrapperValuesInside = wrapper.getExtrapolatedValuesInside(
            position.tickLower,
            position.tickUpper
        );
        baseValue_ += position.unrealizedFees(wrapperValuesInside.sumFeeInsideX128).toInt256();
        baseValue_ += position.unrealizedFundingPayment(
            wrapperValuesInside.sumAX128,
            wrapperValuesInside.sumFpInsideX128
        );
    }
}
","keccak256":"0x86ee3735529365aa5f39b7b4b04a6ac841f9577f79d3a05bc1be34424b46ad06","license":"UNLICENSED"},"contracts/libraries/LiquidityPositionSet.sol":{"content":"//SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.9;

import { Account } from './Account.sol';
import { LiquidityPosition } from './LiquidityPosition.sol';
import { Uint48Lib } from './Uint48.sol';
import { Uint48L5ArrayLib } from './Uint48L5Array.sol';
import { VTokenLib } from './VTokenLib.sol';

import { IClearingHouse } from '../interfaces/IClearingHouse.sol';
import { IVPoolWrapper } from '../interfaces/IVPoolWrapper.sol';
import { IVToken } from '../interfaces/IVToken.sol';

import { console } from 'hardhat/console.sol';

library LiquidityPositionSet {
    using LiquidityPosition for LiquidityPosition.Info;
    using LiquidityPositionSet for Info;
    using Uint48L5ArrayLib for uint48[5];
    using VTokenLib for IVToken;

    struct Info {
        // multiple per pool because it's non-fungible, allows for 4 billion LP positions lifetime
        uint48[5] active;
        // concat(tickLow,tickHigh)
        mapping(uint48 => LiquidityPosition.Info) positions;
        uint256[100] _emptySlots; // reserved for adding variables when upgrading logic
    }

    error IllegalTicks(int24 tickLower, int24 tickUpper);
    error DeactivationFailed(int24 tickLower, int24 tickUpper, uint256 liquidity);
    error InactiveRange();

    function isEmpty(Info storage set) internal view returns (bool) {
        return set.active[0] == 0;
    }

    function isPositionActive(
        Info storage set,
        int24 tickLower,
        int24 tickUpper
    ) internal view returns (bool) {
        return _exists(set.active, tickLower, tickUpper);
    }

    function baseValue(
        Info storage set,
        uint160 sqrtPriceCurrent,
        IVToken vToken,
        Account.ProtocolInfo storage protocol
    ) internal view returns (int256 baseValue_) {
        baseValue_ = set.baseValue(sqrtPriceCurrent, vToken.vPoolWrapper(protocol));
    }

    function baseValue(
        Info storage set,
        uint160 sqrtPriceCurrent,
        IVPoolWrapper wrapper // TODO refactor this
    ) internal view returns (int256 baseValue_) {
        for (uint256 i = 0; i < set.active.length; i++) {
            uint48 id = set.active[i];
            if (id == 0) break;
            baseValue_ += set.positions[id].baseValue(sqrtPriceCurrent, wrapper);
        }
    }

    function maxNetPosition(Info storage set) internal view returns (uint256 risk) {
        for (uint256 i = 0; i < set.active.length; i++) {
            uint48 id = set.active[i];
            risk += set.positions[id].maxNetPosition();
        }
    }

    function getLiquidityPosition(
        Info storage set,
        int24 tickLower,
        int24 tickUpper
    ) internal returns (LiquidityPosition.Info storage position) {
        if (tickLower > tickUpper) {
            revert IllegalTicks(tickLower, tickUpper);
        }

        uint48 positionId = _include(set.active, tickLower, tickUpper);
        position = set.positions[positionId];

        if (!position.isInitialized()) revert InactiveRange();
        return position;
    }

    function activate(
        Info storage set,
        int24 tickLower,
        int24 tickUpper
    ) internal returns (LiquidityPosition.Info storage position) {
        if (tickLower > tickUpper) {
            revert IllegalTicks(tickLower, tickUpper);
        }

        uint48 positionId = _include(set.active, tickLower, tickUpper);
        position = set.positions[positionId];

        if (!position.isInitialized()) {
            position.initialize(tickLower, tickUpper);
        }
    }

    function deactivate(Info storage set, LiquidityPosition.Info storage position) internal {
        if (position.liquidity != 0) {
            revert DeactivationFailed(position.tickLower, position.tickUpper, position.liquidity);
        }

        _exclude(set.active, position.tickLower, position.tickUpper);
    }

    function _include(
        uint48[5] storage array,
        int24 val1,
        int24 val2
    ) private returns (uint48 index) {
        array.include(index = Uint48Lib.concat(val1, val2));
    }

    function _exclude(
        uint48[5] storage array,
        int24 val1,
        int24 val2
    ) private returns (uint48 index) {
        array.exclude(index = Uint48Lib.concat(val1, val2));
    }

    function _exists(
        uint48[5] storage array,
        int24 val1,
        int24 val2
    ) private view returns (bool) {
        return array.exists(Uint48Lib.concat(val1, val2));
    }

    function liquidityChange(
        Info storage set,
        uint256 accountNo,
        IVToken vToken,
        IClearingHouse.LiquidityChangeParams memory liquidityChangeParams,
        IVPoolWrapper wrapper,
        IClearingHouse.BalanceAdjustments memory balanceAdjustments
    ) internal {
        LiquidityPosition.Info storage position = set.activate(
            liquidityChangeParams.tickLower,
            liquidityChangeParams.tickUpper
        );

        position.limitOrderType = liquidityChangeParams.limitOrderType;

        set.liquidityChange(
            accountNo,
            vToken,
            position,
            liquidityChangeParams.liquidityDelta,
            wrapper,
            balanceAdjustments
        );
    }

    function liquidityChange(
        Info storage set,
        uint256 accountNo,
        IVToken vToken,
        LiquidityPosition.Info storage position,
        int128 liquidity,
        IVPoolWrapper wrapper,
        IClearingHouse.BalanceAdjustments memory balanceAdjustments
    ) internal {
        position.liquidityChange(accountNo, vToken, liquidity, wrapper, balanceAdjustments);

        emit Account.LiquidityTokenPositionChange(
            accountNo,
            vToken,
            position.tickLower,
            position.tickUpper,
            balanceAdjustments.vTokenIncrease
        );

        if (position.liquidity == 0) {
            set.deactivate(position);
        }
    }

    function closeLiquidityPosition(
        Info storage set,
        uint256 accountNo,
        IVToken vToken,
        LiquidityPosition.Info storage position,
        IVPoolWrapper wrapper,
        IClearingHouse.BalanceAdjustments memory balanceAdjustments
    ) internal {
        set.liquidityChange(accountNo, vToken, position, -int128(position.liquidity), wrapper, balanceAdjustments);
    }

    function removeLimitOrder(
        Info storage set,
        uint256 accountNo,
        IVToken vToken,
        int24 currentTick,
        int24 tickLower,
        int24 tickUpper,
        IVPoolWrapper wrapper,
        IClearingHouse.BalanceAdjustments memory balanceAdjustments
    ) internal {
        LiquidityPosition.Info storage position = set.getLiquidityPosition(tickLower, tickUpper);
        position.checkValidLimitOrderRemoval(currentTick);
        set.closeLiquidityPosition(accountNo, vToken, position, wrapper, balanceAdjustments);
    }

    function closeAllLiquidityPositions(
        Info storage set,
        uint256 accountNo,
        IVToken vToken,
        IVPoolWrapper wrapper,
        IClearingHouse.BalanceAdjustments memory balanceAdjustments
    ) internal {
        LiquidityPosition.Info storage position;

        while (set.active[0] != 0) {
            IClearingHouse.BalanceAdjustments memory balanceAdjustmentsCurrent;

            position = set.positions[set.active[0]];

            set.closeLiquidityPosition(accountNo, vToken, position, wrapper, balanceAdjustmentsCurrent);

            balanceAdjustments.vBaseIncrease += balanceAdjustmentsCurrent.vBaseIncrease;
            balanceAdjustments.vTokenIncrease += balanceAdjustmentsCurrent.vTokenIncrease;
            balanceAdjustments.traderPositionIncrease += balanceAdjustmentsCurrent.traderPositionIncrease;
        }
    }

    function getView(Info storage set)
        internal
        view
        returns (IClearingHouse.LiquidityPositionView[] memory liquidityPositions)
    {
        uint256 numberOfTokenPositions = set.active.numberOfNonZeroElements();
        liquidityPositions = new IClearingHouse.LiquidityPositionView[](numberOfTokenPositions);

        for (uint256 i = 0; i < numberOfTokenPositions; i++) {
            liquidityPositions[i].limitOrderType = set.positions[set.active[i]].limitOrderType;
            liquidityPositions[i].tickLower = set.positions[set.active[i]].tickLower;
            liquidityPositions[i].tickUpper = set.positions[set.active[i]].tickUpper;
            liquidityPositions[i].liquidity = set.positions[set.active[i]].liquidity;
            liquidityPositions[i].vTokenAmountIn = set.positions[set.active[i]].vTokenAmountIn;
            liquidityPositions[i].sumALastX128 = set.positions[set.active[i]].sumALastX128;
            liquidityPositions[i].sumBInsideLastX128 = set.positions[set.active[i]].sumBInsideLastX128;
            liquidityPositions[i].sumFpInsideLastX128 = set.positions[set.active[i]].sumFpInsideLastX128;
            liquidityPositions[i].sumFeeInsideLastX128 = set.positions[set.active[i]].sumFeeInsideLastX128;
        }
    }
}
