// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.9;

import { VPoolWrapper } from '../../protocol/wrapper/VPoolWrapper.sol';
import { SwapMath } from '../../libraries/SwapMath.sol';

import { console } from 'hardhat/console.sol';

contract VPoolWrapperMock2 is VPoolWrapper {
    uint48 public blockTimestamp;

    constructor() {
        blockTimestamp = uint48(block.timestamp);
    }

    function _blockTimestamp() internal view override returns (uint48) {
        // constructor of VPoolWrapper runs first, there _blockTimestamp() returns zero
        if (blockTimestamp == 0) return uint48(block.timestamp);
        else return blockTimestamp;
    }

    function increaseTimestamp(uint48 secs) external {
        blockTimestamp += secs;
    }

    function calculateFees(int256 amount, SwapMath.AmountTypeEnum amountTypeEnum)
        public
        view
        returns (uint256 liquidityFees, uint256 protocolFees)
    {
        return SwapMath.calculateFees(amount, amountTypeEnum, liquidityFeePips, protocolFeePips);
    }
}

contracts/test/LiquidityPositionTest.sol.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.9;

import { LiquidityPosition } from '../libraries/LiquidityPosition.sol';
import { Account } from '../libraries/Account.sol';
import { VPoolWrapperMock } from './mocks/VPoolWrapperMock.sol';

import { IClearingHouseStructures } from '../interfaces/clearinghouse/IClearingHouseStructures.sol';
import { IVPoolWrapper } from '../interfaces/IVPoolWrapper.sol';

import { AccountProtocolInfoMock } from './mocks/AccountProtocolInfoMock.sol';

import { console } from 'hardhat/console.sol';

contract LiquidityPositionTest is AccountProtocolInfoMock {
    using LiquidityPosition for LiquidityPosition.Info;

    IClearingHouseStructures.BalanceAdjustments public balanceAdjustments;
    LiquidityPosition.Info public lp;
    VPoolWrapperMock public wrapper;

    constructor() {
        wrapper = new VPoolWrapperMock();
    }

    function initialize(int24 tickLower, int24 tickUpper) external {
        lp.initialize(tickLower, tickUpper);
    }

    function updateCheckpoints() external {
        IVPoolWrapper.WrapperValuesInside memory wrapperValuesInside = wrapper.getValuesInside(
            lp.tickLower,
            lp.tickUpper
        );
        lp.update(0, 0, wrapperValuesInside, balanceAdjustments);
    }

    function netPosition(uint160 sqrtPriceCurrent) public view returns (int256) {
        return lp.netPosition(sqrtPriceCurrent);
    }

    function liquidityChange(int128 liquidity) public {
        protocol.pools[0].vPoolWrapper = wrapper;
        protocol.pools[0].vPool = wrapper.vPool();
        lp.liquidityChange(0, 0, liquidity, balanceAdjustments, protocol);
    }

    function maxNetPosition() public view returns (uint256) {
        return lp.maxNetPosition();
    }

    function marketValue(uint160 sqrtPriceCurrent) public view returns (int256) {
        return lp.marketValue(sqrtPriceCurrent, wrapper);
    }
}

contracts/test/CollateralDepositSetTest..content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.9;

import { Uint32L8ArrayLib } from '../libraries/Uint32L8Array.sol';
import { VPoolWrapperMock } from './mocks/VPoolWrapperMock.sol';

import { AddressHelper } from '../libraries/AddressHelper.sol';
import { CollateralDeposit } from '../libraries/CollateralDeposit.sol';

import { IVToken } from '../interfaces/IVToken.sol';
import { IOracle } from '../interfaces/IOracle.sol';
import { IClearingHouseStructures } from '../interfaces/clearinghouse/IClearingHouseStructures.sol';

import { AccountProtocolInfoMock } from './mocks/AccountProtocolInfoMock.sol';

import { IERC20 } from '@openzeppelin/contracts/token/ERC20/IERC20.sol';

contract CollateralDepositSetTest is AccountProtocolInfoMock {
    using CollateralDeposit for CollateralDeposit.Set;
    using AddressHelper for address;
    using AddressHelper for IERC20;
    using Uint32L8ArrayLib for uint32[8];

    CollateralDeposit.Set depositTokenSet;

    VPoolWrapperMock public wrapper;

    constructor(address _settlementToken) {
        wrapper = new VPoolWrapperMock();
        protocol.settlementToken = IERC20(_settlementToken);
    }

    function initVToken(address vToken) external {
        protocol.pools[vToken.truncate()].vToken = IVToken(vToken);
    }

    function init(
        IERC20 cToken,
        IOracle oracle,
        uint32 twapDuration
    ) external {
        IClearingHouseStructures.Collateral memory collateral = IClearingHouseStructures.Collateral(
            cToken,
            IClearingHouseStructures.CollateralSettings(oracle, twapDuration, true)
        );
        protocol.collaterals[collateral.token.truncate()] = collateral;
    }

    function cleanDeposits() external {
        for (uint256 i = 0; i < depositTokenSet.active.length; i++) {
            uint32 collateralId = depositTokenSet.active[i];
            if (collateralId == 0) break;

            depositTokenSet.decreaseBalance(collateralId, depositTokenSet.deposits[collateralId]);
        }
    }

    function increaseBalance(address realTokenAddress, uint256 amount) external {
        depositTokenSet.increaseBalance(realTokenAddress.truncate(), amount);
    }

    function decreaseBalance(address realTokenAddress, uint256 amount) external {
        depositTokenSet.decreaseBalance(realTokenAddress.truncate(), amount);
    }

    function getAllDepositAccountMarketValue() external view returns (int256 depositValue) {
        return depositTokenSet.marketValue(protocol);
    }

    function getBalance(address realTokenAddress) external view returns (uint256 balance) {
        return depositTokenSet.deposits[realTokenAddress.truncate()];
    }
}

